package com.magiclon.unitstrokeproject.fragment;import android.annotation.SuppressLint;import android.content.Context;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.support.v4.widget.SwipeRefreshLayout;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.magiclon.unitstrokeproject.R;import com.magiclon.unitstrokeproject.adapter.PeopleAdapter;import java.util.ArrayList;/** * Created by Administrator on 2016/7/25 0025. */@SuppressLint("ValidFragment")public class ListFragment extends BaseFragment {    private int type = 0;    public ListFragment(int type) {        this.type = type;    }    private View view;    private RecyclerView mListView;    Context mContext;    @Override    public void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        mContext = getActivity();    }    @Override    public void fetchData() {    }    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {        view = inflater.inflate(R.layout.list_layout, container, false);        initView();        return view;    }    private void initView() {        mListView = view.findViewById(R.id.list_view);        mListView.setLayoutManager(new LinearLayoutManager(mContext));        mListView.setAdapter(new PeopleAdapter(type));    }}